import React from 'react';
import { Container, Typography, Button, Box, Fade } from '@mui/material';

function Hero() {
  return (
    <Fade in timeout={1000}>
      <Box
        sx={{
          paddingY: 10,
          textAlign: 'center',
          color: '#333333',
          backgroundColor: '#f2f4f8',
          backgroundImage: 'url("path-to-subtle-water-background.jpg")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <Container maxWidth="md">
          <Typography
            variant="h3"
            sx={{ fontWeight: 'bold', marginBottom: 3 }}
          >
            Cast Your Line, Win Your Prize
          </Typography>
          <Typography
            variant="body1"
            sx={{ fontSize: '1.2rem', marginBottom: 5, color: '#6c757d' }}
          >
            Join tournaments from anywhere, win big, and connect with anglers worldwide.
          </Typography>
          <Button
            variant="contained"
            size="large"
            href="#signup"
            sx={{
              backgroundColor: '#2a5e4f',
              color: '#ffffff',
              paddingX: 4,
              paddingY: 1.5,
              borderRadius: '12px',
              '&:hover': { backgroundColor: '#20473b' },
            }}
          >
            Get Started
          </Button>
        </Container>
      </Box>
    </Fade>
  );
}

export default Hero;
