import React from 'react';
import { Container, Typography, Button, Box } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore'; // For the scroll prompt

function Hero() {
  return (
    <Box
      sx={{
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        color: '#ffffff',
        backgroundImage: 'url("path-to-your-background-image.jpg")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        position: 'relative',
      }}
    >
      <Box
        sx={{
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Slight overlay to make text pop
          padding: '20px',
          borderRadius: '15px',
        }}
      >
        <Typography
          variant="h3"
          sx={{
            fontWeight: 'bold',
            fontSize: { xs: '2rem', md: '3rem' },
            marginBottom: 2,
            fontFamily: 'Poppins, sans-serif',
          }}
        >
          Transform Your Passion into Victory
        </Typography>
        <Typography
          variant="body1"
          sx={{
            fontSize: { xs: '1.1rem', md: '1.5rem' },
            marginBottom: 4,
            fontFamily: 'Roboto, sans-serif',
          }}
        >
          Join tournaments from anywhere, submit your catch, and win cash prizes.
        </Typography>
        <Button
          variant="contained"
          size="large"
          href="#signup"
          sx={{
            backgroundColor: '#2a5e4f',
            color: '#ffffff',
            paddingX: 4,
            paddingY: 1.5,
            borderRadius: '25px',
            boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
            '&:hover': { backgroundColor: '#20473b' },
            fontFamily: 'Roboto, sans-serif',
          }}
        >
          Get Started
        </Button>
      </Box>
      <Box
        sx={{
          position: 'absolute',
          bottom: 20,
          textAlign: 'center',
          cursor: 'pointer',
        }}
        onClick={() => {
          window.scrollTo({
            top: document.getElementById('highlights-section').offsetTop,
            behavior: 'smooth',
          });
        }}
      >
        <Typography variant="body2" sx={{ color: '#ffffff', marginBottom: 1 }}>
          Scroll Down
        </Typography>
        <ExpandMoreIcon sx={{ fontSize: 30, color: '#ffffff' }} />
      </Box>
    </Box>
  );
}

export default Hero;
