import React from 'react';
import { Container, Typography, Button, Box } from '@mui/material';
import HeroImage from './Hero_Image.jpg'; // Ensure this path is correct

function Hero() {
  return (
    <Box
      sx={{
        height: '75vh', // Adjusted height
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        backgroundImage: `url(${HeroImage})`, // Correctly referencing the imported image
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        position: 'relative',
        color: '#ffffff',
        borderRadius: '15px'
      }}
    >
      <Box
        sx={{
          position: 'absolute',
          inset: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.4)', // Overlay for text readability
        }}
      ></Box>
      <Container
        maxWidth="sm"
        sx={{
          position: 'relative',
          zIndex: 1,
        }}
      >
        <Typography
          variant="h4"
          sx={{
            fontWeight: 'bold',
            fontSize: { xs: '2rem', md: '3rem' },
            marginBottom: 2,
            fontFamily: 'Poppins, sans-serif',
          }}
        >
          Transform Your Passion into Victory
        </Typography>
        <Typography
          variant="body1"
          sx={{
            fontSize: { xs: '1rem', md: '1.25rem' },
            marginBottom: 4,
            fontFamily: 'Roboto, sans-serif',
          }}
        >
          Join tournaments from anywhere, submit your catch, and win cash prizes.
        </Typography>
        <Button
          variant="contained"
          size="large"
          href="#signup"
          sx={{
            backgroundColor: '#d6b98d', // Adjusted button color
            color: '#ffffff',
            paddingX: 4,
            paddingY: 1.5,
            borderRadius: '30px',
            fontFamily: 'Roboto, sans-serif',
            '&:hover': { backgroundColor: '#b3946d' }, // Hover effect
          }}
        >
          Get Started
        </Button>
      </Container>
    </Box>
  );
}

export default Hero;
