import React from 'react';
import { Container, Typography, Grid, Box } from '@mui/material';
import InfoCard from './InfoCard'; // Assume you have an InfoCard component for each feature

function InfoSection() {
  return (
    <Container maxWidth="lg" sx={{ paddingY: 5 }}>
      <Typography variant="h4" sx={{ color: '#132a47', textAlign: 'center', fontWeight: 'bold', marginBottom: 4 }}>
        Why Choose Our Fishing App?
      </Typography>
      <Grid container spacing={4}>
        <InfoCard title="Variety of Tournaments" description="Choose from species-specific, location-specific, or open tournaments." />
        <InfoCard title="Real-time Scoring" description="Upload your catches and see how you rank instantly on our leaderboards." />
        <InfoCard title="Anti-Cheating Measures" description="Advanced geolocation, photo-tampering detection, and secure measuring devices ensure fair play." />
        <InfoCard title="Secure Transactions" description="Safe and reliable payment methods for prize distribution." />
      </Grid>
    </Container>
  );
}

export default InfoSection;
