import React from 'react';
import { Container, Typography, Button, Box } from '@mui/material';

function Hero() {
  return (
    <Box
      sx={{
        backgroundColor: '#2a5e4f',
        paddingY: 8,
        textAlign: 'center',
        color: '#ffffff',
        backgroundImage: 'linear-gradient(to right, #2a5e4f, #132a47)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
      }}
    >
      <Container maxWidth="md">
        <Typography
          variant="h4"
          sx={{ fontWeight: 'bold', marginBottom: 3, fontSize: { xs: '2rem', md: '3rem' }, lineHeight: 1.2 }}
        >
          Compete in the most trusted online fishing tournaments and win big from anywhere
        </Typography>
        <Typography
          variant="body1"
          sx={{ fontSize: { xs: '1rem', md: '1.5rem' }, marginBottom: 5 }}
        >
          Join tournaments at the click of a button and fish alongside anglers from around the world.
        </Typography>
        <Button
          variant="contained"
          size="large"
          href="#signup"
          sx={{
            backgroundColor: '#d6b98d',
            color: '#132a47',
            paddingX: 4,
            paddingY: 1.5,
            fontSize: { xs: '0.9rem', md: '1rem' },
            '&:hover': { backgroundColor: '#132a47', color: '#d6b98d' },
          }}
        >
          Get Started
        </Button>
      </Container>
    </Box>
  );
}

export default Hero;
