import React from 'react';
import { Container, Typography, Button, Box } from '@mui/material';

function Hero() {
  return (
    <Box
      sx={{
        paddingY: { xs: 6, md: 10 },
        textAlign: 'center',
        color: '#333333',
        backgroundColor: '#f2f4f8',
        backgroundImage: 'linear-gradient(to bottom right, #f2f4f8, #e0e7ff)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      <Container maxWidth="sm">
        <Typography
          variant="h4"
          sx={{
            fontWeight: 'bold',
            fontSize: { xs: '1.8rem', md: '2.5rem' },
            fontFamily: 'Poppins, sans-serif',
            marginBottom: 2,
            color: '#2a5e4f',
          }}
        >
          Transform Your Passion into Victory
        </Typography>
        <Typography
          variant="body1"
          sx={{
            fontSize: { xs: '1rem', md: '1.25rem' },
            marginBottom: 4,
            color: '#6c757d',
            fontFamily: 'Roboto, sans-serif',
          }}
        >
          Join tournaments from anywhere, submit your catch, and win cash prizes.
        </Typography>
        <Button
          variant="contained"
          size="large"
          href="#signup"
          sx={{
            backgroundColor: '#2a5e4f',
            color: '#ffffff',
            paddingX: 4,
            paddingY: 1.5,
            borderRadius: '25px',
            boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
            '&:hover': { backgroundColor: '#20473b' },
            fontFamily: 'Roboto, sans-serif',
          }}
        >
          Get Started
        </Button>
      </Container>
    </Box>
  );
}

export default Hero;
