import React from 'react';
import { Container, Typography, Button, Box } from '@mui/material';

function Hero() {
  return (
    <Box
      sx={{
        backgroundColor: '#00522D', // Deep green
        paddingY: 10,
        textAlign: 'center',
        color: '#ffffff',
        backgroundImage: 'linear-gradient(to right, #00522D, #102539)', // Gradual blend from green to dark blue
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
      }}
    >
      <Container maxWidth="md">
        <Typography variant="h2" gutterBottom sx={{ fontWeight: 'bold', marginBottom: 3 }}>
          Join the Best Online Fishing Tournaments
        </Typography>
        <Typography variant="body1" gutterBottom sx={{ fontSize: '1.2rem', marginBottom: 5 }}>
          Compete, win prizes, and enjoy a seamless experience from anywhere, anytime.
        </Typography>
        <Button variant="contained" size="large" href="#signup" sx={{ backgroundColor: '#1e88e5', paddingX: 5, paddingY: 1.5, '&:hover': { backgroundColor: '#1565c0' } }}>
          Get Started
        </Button>
      </Container>
    </Box>
  );
}

export default Hero;
